// 1) Senior-level keywords to exclude
const excludeKeywords = [
  "senior",
  "sr\\.?\\b",
  "principal",
  "director",
  "head",
  "vp",
  "vice president",
  "chief",
  "staff",
  "lead\\b",
  "manager\\b",
  "scientist",
  "architect",
  "engineer",
  "developer",
  "phd",
  "grad",
  "postdoc",
  "legal"
].map(k => new RegExp(`\\b${k}`, "i"));

const now = new Date();
const twoMonthsAgo = new Date(now);
twoMonthsAgo.setMonth(now.getMonth() - 2);
const currentYear = now.getFullYear();

return items.filter(item => {
  const title = (item.json.title || "").trim();

  // 1) Drop anything with a senior-level hint
  if (excludeKeywords.some(regex => regex.test(title))) {
    return false;
  }

  // 2) Parse publication date
  const pubDate = item.json.pubDate ? new Date(item.json.pubDate) : null;
  if (!(pubDate instanceof Date) || isNaN(pubDate)) {
    return false;
  }

  // 3) Enforce "this year" AND "within two months"
  return pubDate.getFullYear() === currentYear
      && pubDate >= twoMonthsAgo;
});

const [resumeData, jobData] = items.map(i => i.json);

// Flatten resumeData.content into resume directly
const flattenedResume = resumeData.content || resumeData;

return [
  {
    json: {
      job: jobData,
      resume: flattenedResume
    }
  }
];

// 1. Collect all existing Job_IDs from Google Sheet (normalize as strings)
const existingJobIds = $items("Check Job Ids")
  .map(item => item.json["Job ID"])
  .filter(id => id !== undefined && id !== null)
  .map(id => id.toString().trim());

console.log('Existing Job IDs from Sheet:', existingJobIds);

// 2. Filter job postings by checking if the job ID (from link) exists in the sheet
const filtered = $items("Extract Job Postings").filter(item => {
  const link = item.json.link;
  if (!link) return false;

  // Extract the job ID from the end of the link
  const jobId = link.split('-').pop()?.trim();

  console.log(`Checking Job ID from link "${link}":`, jobId);

  // Return only if this ID is NOT in the existing ones
  return jobId && !existingJobIds.includes(jobId);
});

console.log(`Filtered ${filtered.length} new job(s) out of ${$items("Extract Job Postings").length}.`);

return filtered;

return items.map(item => {
  const parsed = JSON.parse(item.json.content); // Parse stringified JSON from the sheet

  return {
    json: {
      resume: {
        text_summary: parsed.text_summary || "",
        skills: {
          technical: parsed.skills?.technical || [],
          transferable: parsed.skills?.transferable || [],
          soft: parsed.skills?.soft || []
        },
        total_yoe: parsed.total_yoe || "",
        tech_yoe: parsed.tech_yoe || "",
        experience: parsed.experience || []
      }
    }
  };
});
